bool demo::loaded = false;

#ifdef __cplusplus
extern "C" {
#endif

    duk_ret_t cpp(duk_context *ctx) {
        if (!demo::loaded) {
            dukglue_register_constructor(ctx, "demo");
            dukglue_register_method(ctx, &demo::echo, "echo");
            demo::loaded = true;
        }
        if (demo::loaded) {
            duk_push_true(ctx);
        } else {
            duk_push_false(ctx);
        }
        return 1; /* one return value */
    }

#ifdef __cplusplus
}
#endif

